import { Slider } from '@/components/ui/slider';
import { Label } from '@/components/ui/label';
import { Button } from '@/components/ui/button';
import { Switch } from '@/components/ui/switch';
import { RotateCcw } from 'lucide-react';

interface AdjustmentPanelProps {
  brightness: number;
  onBrightnessChange: (value: number) => void;
  contrast: number;
  onContrastChange: (value: number) => void;
  saturation: number;
  onSaturationChange: (value: number) => void;
  grayscale: boolean;
  onGrayscaleChange: (value: boolean) => void;
  sepia: boolean;
  onSepiaChange: (value: boolean) => void;
  blur: number;
  onBlurChange: (value: number) => void;
  sharpen: number;
  onSharpenChange: (value: number) => void;
  onReset: () => void;
}

export default function AdjustmentPanel({
  brightness,
  onBrightnessChange,
  contrast,
  onContrastChange,
  saturation,
  onSaturationChange,
  grayscale,
  onGrayscaleChange,
  sepia,
  onSepiaChange,
  blur,
  onBlurChange,
  sharpen,
  onSharpenChange,
  onReset
}: AdjustmentPanelProps) {
  return (
    <div className="w-80 bg-card border-l border-border p-6 space-y-6" data-testid="panel-adjustments">
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold">Adjustments</h3>
        <Button
          size="icon"
          variant="ghost"
          onClick={onReset}
          data-testid="button-reset-adjustments"
        >
          <RotateCcw className="w-4 h-4" />
        </Button>
      </div>

      <div className="space-y-6">
        {/* Brightness */}
        <div className="space-y-3">
          <div className="flex items-center justify-between">
            <Label htmlFor="brightness" className="text-sm font-medium">
              Brightness
            </Label>
            <span className="text-sm text-muted-foreground" data-testid="text-brightness-value">
              {brightness}%
            </span>
          </div>
          <Slider
            id="brightness"
            value={[brightness]}
            onValueChange={(value) => onBrightnessChange(value[0])}
            min={0}
            max={200}
            step={1}
            className="w-full"
            data-testid="slider-brightness"
          />
        </div>

        {/* Contrast */}
        <div className="space-y-3">
          <div className="flex items-center justify-between">
            <Label htmlFor="contrast" className="text-sm font-medium">
              Contrast
            </Label>
            <span className="text-sm text-muted-foreground" data-testid="text-contrast-value">
              {contrast}%
            </span>
          </div>
          <Slider
            id="contrast"
            value={[contrast]}
            onValueChange={(value) => onContrastChange(value[0])}
            min={0}
            max={200}
            step={1}
            className="w-full"
            data-testid="slider-contrast"
          />
        </div>

        {/* Saturation */}
        <div className="space-y-3">
          <div className="flex items-center justify-between">
            <Label htmlFor="saturation" className="text-sm font-medium">
              Saturation
            </Label>
            <span className="text-sm text-muted-foreground" data-testid="text-saturation-value">
              {saturation}%
            </span>
          </div>
          <Slider
            id="saturation"
            value={[saturation]}
            onValueChange={(value) => onSaturationChange(value[0])}
            min={0}
            max={200}
            step={1}
            className="w-full"
            data-testid="slider-saturation"
          />
        </div>

        {/* Filters */}
        <div className="space-y-4 pt-2 border-t border-border">
          <Label className="text-sm font-medium">Filters</Label>

          {/* Grayscale */}
          <div className="flex items-center justify-between">
            <Label htmlFor="grayscale" className="text-sm font-medium">
              Grayscale
            </Label>
            <Switch
              id="grayscale"
              checked={grayscale}
              onCheckedChange={onGrayscaleChange}
              data-testid="switch-grayscale"
            />
          </div>

          {/* Sepia */}
          <div className="flex items-center justify-between">
            <Label htmlFor="sepia" className="text-sm font-medium">
              Sepia
            </Label>
            <Switch
              id="sepia"
              checked={sepia}
              onCheckedChange={onSepiaChange}
              data-testid="switch-sepia"
            />
          </div>

          {/* Blur */}
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <Label htmlFor="blur" className="text-sm font-medium">
                Blur
              </Label>
              <span className="text-sm text-muted-foreground" data-testid="text-blur-value">
                {blur}px
              </span>
            </div>
            <Slider
              id="blur"
              value={[blur]}
              onValueChange={(value) => onBlurChange(value[0])}
              min={0}
              max={10}
              step={0.5}
              className="w-full"
              data-testid="slider-blur"
            />
          </div>

          {/* Sharpen */}
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <Label htmlFor="sharpen" className="text-sm font-medium">
                Sharpen
              </Label>
              <span className="text-sm text-muted-foreground" data-testid="text-sharpen-value">
                {sharpen}%
              </span>
            </div>
            <Slider
              id="sharpen"
              value={[sharpen]}
              onValueChange={(value) => onSharpenChange(value[0])}
              min={0}
              max={200}
              step={10}
              className="w-full"
              data-testid="slider-sharpen"
            />
          </div>
        </div>
      </div>

      {/* Quick Presets */}
      <div className="space-y-3">
        <Label className="text-sm font-medium">Quick Presets</Label>
        <div className="grid grid-cols-2 gap-2">
          <Button
            variant="secondary"
            size="sm"
            onClick={() => {
              onBrightnessChange(110);
              onContrastChange(120);
              onSaturationChange(110);
            }}
            data-testid="button-preset-vivid"
          >
            Vivid
          </Button>
          <Button
            variant="secondary"
            size="sm"
            onClick={() => {
              onBrightnessChange(90);
              onContrastChange(80);
              onSaturationChange(60);
            }}
            data-testid="button-preset-vintage"
          >
            Vintage
          </Button>
          <Button
            variant="secondary"
            size="sm"
            onClick={() => {
              onBrightnessChange(85);
              onContrastChange(130);
              onSaturationChange(120);
            }}
            data-testid="button-preset-dramatic"
          >
            Dramatic
          </Button>
          <Button
            variant="secondary"
            size="sm"
            onClick={() => {
              onBrightnessChange(105);
              onContrastChange(95);
              onSaturationChange(80);
            }}
            data-testid="button-preset-soft"
          >
            Soft
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/components/EditingToolbar.tsx.content:
import { useState } from 'react';
import { 
  Crop, 
  RotateCw, 
  FlipHorizontal, 
  FlipVertical, 
  Download,
  Undo,
  Redo,
  Move,
  Maximize
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Separator } from '@/components/ui/separator';
import { Badge } from '@/components/ui/badge';

export type Tool = 'move' | 'crop' | 'rotate' | 'flip-h' | 'flip-v';

interface EditingToolbarProps {
  selectedTool: Tool;
  onToolSelect: (tool: Tool) => void;
  onAction: (action: string) => void;
  canUndo: boolean;
  canRedo: boolean;
  zoom: number;
  imageDimensions?: { width: number; height: number };
}

export default function EditingToolbar({
  selectedTool,
  onToolSelect,
  onAction,
  canUndo,
  canRedo,
  zoom,
  imageDimensions
}: EditingToolbarProps) {
  const [isHovered, setIsHovered] = useState<string | null>(null);

  const tools = [
    { id: 'move' as Tool, icon: Move, label: 'Move' },
    { id: 'crop' as Tool, icon: Crop, label: 'Crop' },
  ];

  const actions = [
    { id: 'rotate', icon: RotateCw, label: 'Rotate' },
    { id: 'flip-horizontal', icon: FlipHorizontal, label: 'Flip H' },
    { id: 'flip-vertical', icon: FlipVertical, label: 'Flip V' },
  ];

  return (
    <div className="bg-card border-b border-border p-2 flex items-center justify-between sticky top-0 z-10">
      <div className="flex items-center space-x-2">
        {/* Undo/Redo */}
        <div className="flex items-center space-x-1">
          <Button
            size="icon"
            variant="ghost"
            disabled={!canUndo}
            onClick={() => onAction('undo')}
            data-testid="button-undo"
            onMouseEnter={() => setIsHovered('undo')}
            onMouseLeave={() => setIsHovered(null)}
          >
            <Undo className="w-4 h-4" />
          </Button>
          <Button
            size="icon"
            variant="ghost"
            disabled={!canRedo}
            onClick={() => onAction('redo')}
            data-testid="button-redo"
            onMouseEnter={() => setIsHovered('redo')}
            onMouseLeave={() => setIsHovered(null)}
          >
            <Redo className="w-4 h-4" />
          </Button>
        </div>

        <Separator orientation="vertical" className="h-6" />

        {/* Selection Tools */}
        <div className="flex items-center space-x-1">
          {tools.map(({ id, icon: Icon, label }) => (
            <Button
              key={id}
              size="icon"
              variant={selectedTool === id ? "default" : "ghost"}
              onClick={() => onToolSelect(id)}
              data-testid={`button-tool-${id}`}
              onMouseEnter={() => setIsHovered(label)}
              onMouseLeave={() => setIsHovered(null)}
            >
              <Icon className="w-4 h-4" />
            </Button>
          ))}
        </div>

        <Separator orientation="vertical" className="h-6" />

        {/* Transform Actions */}
        <div className="flex items-center space-x-1">
          {actions.map(({ id, icon: Icon, label }) => (
            <Button
              key={id}
              size="icon"
              variant="ghost"
              onClick={() => onAction(id)}
              data-testid={`button-action-${id}`}
              onMouseEnter={() => setIsHovered(label)}
              onMouseLeave={() => setIsHovered(null)}
            >
              <Icon className="w-4 h-4" />
            </Button>
          ))}
        </div>
      </div>

      {/* Info Display */}
      <div className="flex items-center space-x-4">
        {imageDimensions && (
          <Badge variant="secondary" data-testid="text-dimensions">
            {imageDimensions.width} × {imageDimensions.height}
          </Badge>
        )}

        <Badge variant="secondary" data-testid="text-zoom">
          {Math.round(zoom * 100)}%
        </Badge>

        <Button
          variant="default"
          size="sm"
          onClick={() => onAction('download')}
          data-testid="button-download"
          className="gap-2"
        >
          <Download className="w-4 h-4" />
          Export
        </Button>
      </div>

      {/* Tooltip */}
      {isHovered && (
        <div className="fixed z-50 bg-popover text-popover-foreground px-2 py-1 rounded text-xs pointer-events-none"
             style={{ 
               left: '50%', 
               transform: 'translateX(-50%)',
               top: '60px'
             }}>
          {isHovered}
        </div>
      )}
    </div>
  );
}

client/src/components/ImageCanvas.tsx.content:
import { useRef, useEffect, useState, useCallback } from 'react';
import { ZoomIn, ZoomOut, Maximize2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Tool } from './EditingToolbar';

interface ImageCanvasProps {
  imageUrl?: string;
  zoom: number;
  onZoomChange: (zoom: number) => void;
  onImageDimensionsChange: (dimensions: { width: number; height: number }) => void;
  brightness: number;
  contrast: number;
  saturation: number;
  rotation: number;
  flipHorizontal: boolean;
  flipVertical: boolean;
  grayscale: boolean;
  sepia: boolean;
  blur: number;
  sharpen: number;
  selectedTool: Tool;
  onCropApply: (cropData: { x: number; y: number; width: number; height: number }) => void;
}

export default function ImageCanvas({
  imageUrl,
  zoom,
  onZoomChange,
  onImageDimensionsChange,
  brightness,
  contrast,
  saturation,
  rotation,
  flipHorizontal,
  flipVertical,
  grayscale,
  sepia,
  blur,
  sharpen,
  selectedTool,
  onCropApply
}: ImageCanvasProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const [image, setImage] = useState<HTMLImageElement | null>(null);
  const [isPanning, setIsPanning] = useState(false);
  const [canvasOffset, setCanvasOffset] = useState({ x: 0, y: 0 });
  const [isCropping, setIsCropping] = useState(false);
  const [cropStart, setCropStart] = useState<{ x: number; y: number } | null>(null);
  const [cropEnd, setCropEnd] = useState<{ x: number; y: number } | null>(null);
  const overlayCanvasRef = useRef<HTMLCanvasElement>(null);

  // Load image
  useEffect(() => {
    if (!imageUrl) return;

    const img = new Image();
    img.onload = () => {
      setImage(img);
      onImageDimensionsChange({ width: img.naturalWidth, height: img.naturalHeight });
    };
    img.src = imageUrl;
  }, [imageUrl, onImageDimensionsChange]);

  // Apply all filters using ctx.filter for perfect fidelity (same as export function)
  const applyAllFilters = useCallback((ctx: CanvasRenderingContext2D, brightness: number, contrast: number, saturation: number, grayscale: boolean, sepia: boolean, blur: number, sharpen: number) => {
    const filters = [];

    // Basic adjustments
    if (brightness !== 100) filters.push(`brightness(${brightness}%)`);
    if (contrast !== 100 || sharpen > 0) {
      // Approximate sharpen as additional contrast for simplicity
      const totalContrast = contrast + (sharpen * 0.5); // sharpen contributes to contrast
      filters.push(`contrast(${totalContrast}%)`);
    }
    if (saturation !== 100) filters.push(`saturate(${saturation}%)`);

    // Filter effects
    if (grayscale) filters.push('grayscale(100%)');
    if (sepia) filters.push('sepia(100%)');
    if (blur > 0) filters.push(`blur(${blur}px)`);

    ctx.filter = filters.length > 0 ? filters.join(' ') : 'none';
  }, []);

  // Calculate rotated canvas dimensions
  const getRotatedDimensions = useCallback((width: number, height: number, rotation: number) => {
    const rad = (rotation * Math.PI) / 180;
    const cos = Math.abs(Math.cos(rad));
    const sin = Math.abs(Math.sin(rad));

    return {
      width: Math.ceil(width * cos + height * sin),
      height: Math.ceil(width * sin + height * cos)
    };
  }, []);

  // Draw image with real processing
  const drawImage = useCallback(() => {
    if (!image || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Calculate base dimensions
    const baseWidth = image.naturalWidth;
    const baseHeight = image.naturalHeight;

    // Calculate dimensions after rotation
    const rotatedDims = getRotatedDimensions(baseWidth, baseHeight, rotation);

    // Apply zoom to final dimensions
    const displayWidth = rotatedDims.width * zoom;
    const displayHeight = rotatedDims.height * zoom;

    canvas.width = displayWidth;
    canvas.height = displayHeight;

    // Create temporary canvas for processing
    const tempCanvas = document.createElement('canvas');
    const tempCtx = tempCanvas.getContext('2d');
    if (!tempCtx) return;

    tempCanvas.width = baseWidth;
    tempCanvas.height = baseHeight;

    // Apply all filters using ctx.filter (same as export function)
    applyAllFilters(tempCtx, brightness, contrast, saturation, grayscale, sepia, blur, sharpen);

    // Draw original image to temp canvas with filter applied
    tempCtx.drawImage(image, 0, 0);
    tempCtx.filter = 'none'; // Reset filter

    // Clear canvas with transparent background
    ctx.clearRect(0, 0, displayWidth, displayHeight);

    // Apply transformations to main canvas
    ctx.save();
    ctx.translate(displayWidth / 2, displayHeight / 2);
    ctx.rotate((rotation * Math.PI) / 180);
    ctx.scale(flipHorizontal ? -1 : 1, flipVertical ? -1 : 1);

    // Draw the processed image
    ctx.drawImage(
      tempCanvas,
      -(baseWidth * zoom) / 2,
      -(baseHeight * zoom) / 2,
      baseWidth * zoom,
      baseHeight * zoom
    );

    ctx.restore();
  }, [image, zoom, brightness, contrast, saturation, rotation, flipHorizontal, flipVertical, grayscale, sepia, blur, sharpen, applyAllFilters, getRotatedDimensions]);

  useEffect(() => {
    drawImage();
  }, [drawImage]);

  // Helper function to transform image coordinates to canvas coordinates
  const imageToCanvasCoords = useCallback((imageX: number, imageY: number) => {
    if (!image) return { x: 0, y: 0 };

    const baseWidth = image.naturalWidth;
    const baseHeight = image.naturalHeight;
    const rotatedDims = getRotatedDimensions(baseWidth, baseHeight, rotation);

    const displayWidth = rotatedDims.width * zoom;
    const displayHeight = rotatedDims.height * zoom;

    // Convert image coordinates to centered coordinates
    let x = (imageX - baseWidth / 2) * zoom;
    let y = (imageY - baseHeight / 2) * zoom;

    // Apply the same transformations as the main canvas
    // 1. Rotation
    if (rotation !== 0) {
      const rad = (rotation * Math.PI) / 180;
      const cos = Math.cos(rad);
      const sin = Math.sin(rad);
      const rotatedX = x * cos - y * sin;
      const rotatedY = x * sin + y * cos;
      x = rotatedX;
      y = rotatedY;
    }

    // 2. Scale (flip)
    if (flipHorizontal) x = -x;
    if (flipVertical) y = -y;

    // 3. Translate to canvas center
    x += displayWidth / 2;
    y += displayHeight / 2;

    return { x, y };
  }, [image, zoom, rotation, flipHorizontal, flipVertical, getRotatedDimensions]);

  // Draw crop overlay with proper transformation support
  const drawCropOverlay = useCallback(() => {
    if (!overlayCanvasRef.current || !canvasRef.current || !cropStart || !cropEnd) return;

    const overlayCanvas = overlayCanvasRef.current;
    const mainCanvas = canvasRef.current;
    const ctx = overlayCanvas.getContext('2d');
    if (!ctx) return;

    // Match overlay canvas size to main canvas
    overlayCanvas.width = mainCanvas.width;
    overlayCanvas.height = mainCanvas.height;

    ctx.clearRect(0, 0, overlayCanvas.width, overlayCanvas.height);

    // Calculate all four corners of the crop rectangle in image space
    const minX = Math.min(cropStart.x, cropEnd.x);
    const minY = Math.min(cropStart.y, cropEnd.y);
    const maxX = Math.max(cropStart.x, cropEnd.x);
    const maxY = Math.max(cropStart.y, cropEnd.y);

    // Transform all four corners to canvas space
    const topLeft = imageToCanvasCoords(minX, minY);
    const topRight = imageToCanvasCoords(maxX, minY);
    const bottomRight = imageToCanvasCoords(maxX, maxY);
    const bottomLeft = imageToCanvasCoords(minX, maxY);

    // Draw crop rectangle as a polygon to handle rotation properly
    ctx.strokeStyle = '#3B82F6';
    ctx.lineWidth = 2;
    ctx.setLineDash([5, 5]);

    ctx.beginPath();
    ctx.moveTo(topLeft.x, topLeft.y);
    ctx.lineTo(topRight.x, topRight.y);
    ctx.lineTo(bottomRight.x, bottomRight.y);
    ctx.lineTo(bottomLeft.x, bottomLeft.y);
    ctx.closePath();
    ctx.stroke();

    // Draw corner handles at the transformed positions
    ctx.fillStyle = '#3B82F6';
    ctx.setLineDash([]);
    const handleSize = 8;

    [topLeft, topRight, bottomRight, bottomLeft].forEach(corner => {
      ctx.fillRect(corner.x - handleSize/2, corner.y - handleSize/2, handleSize, handleSize);
    });
  }, [cropStart, cropEnd, imageToCanvasCoords]);

  useEffect(() => {
    if (selectedTool === 'crop' && (cropStart || cropEnd)) {
      drawCropOverlay();
    }
  }, [selectedTool, cropStart, cropEnd, drawCropOverlay, zoom, rotation, flipHorizontal, flipVertical]);

  const handleZoomIn = () => {
    const newZoom = Math.min(zoom * 1.25, 5);
    onZoomChange(newZoom);
  };

  const handleZoomOut = () => {
    const newZoom = Math.max(zoom * 0.8, 0.1);
    onZoomChange(newZoom);
  };

  const handleFitToScreen = () => {
    if (!image || !containerRef.current) return;

    const container = containerRef.current;
    const containerWidth = container.clientWidth - 40;
    const containerHeight = container.clientHeight - 40;

    const scaleX = containerWidth / image.naturalWidth;
    const scaleY = containerHeight / image.naturalHeight;
    const newZoom = Math.min(scaleX, scaleY, 1);

    onZoomChange(newZoom);
    setCanvasOffset({ x: 0, y: 0 });
  };

  // Convert screen coordinates to image coordinates
  const screenToImageCoords = useCallback((screenX: number, screenY: number) => {
    if (!canvasRef.current || !image) return { x: 0, y: 0 };

    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const canvasX = screenX - rect.left;
    const canvasY = screenY - rect.top;

    // Account for canvas scaling and centering
    const baseWidth = image.naturalWidth;
    const baseHeight = image.naturalHeight;
    const rotatedDims = getRotatedDimensions(baseWidth, baseHeight, rotation);

    const displayWidth = rotatedDims.width * zoom;
    const displayHeight = rotatedDims.height * zoom;

    // Convert canvas coordinates to canvas-centered coordinates
    let x = canvasX - displayWidth / 2;
    let y = canvasY - displayHeight / 2;

    // Apply inverse transformations in reverse order (same as main canvas but reversed)
    // 1. Inverse scale (flip)
    if (flipHorizontal) x = -x;
    if (flipVertical) y = -y;

    // 2. Inverse rotation
    if (rotation !== 0) {
      const rad = -(rotation * Math.PI) / 180;
      const cos = Math.cos(rad);
      const sin = Math.sin(rad);
      const rotatedX = x * cos - y * sin;
      const rotatedY = x * sin + y * cos;
      x = rotatedX;
      y = rotatedY;
    }

    // 3. Convert back to image coordinates
    const imageX = (x / zoom) + baseWidth / 2;
    const imageY = (y / zoom) + baseHeight / 2;

    return {
      x: Math.max(0, Math.min(baseWidth, imageX)),
      y: Math.max(0, Math.min(baseHeight, imageY))
    };
  }, [image, zoom, rotation, flipHorizontal, flipVertical, getRotatedDimensions]);

  const handleMouseDown = (e: React.MouseEvent) => {
    if (e.button === 0) {
      if (selectedTool === 'crop') {
        const coords = screenToImageCoords(e.clientX, e.clientY);
        setCropStart(coords);
        setCropEnd(coords);
        setIsCropping(true);
      } else {
        setIsPanning(true);
      }
    }
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (isPanning && selectedTool !== 'crop') {
      setCanvasOffset(prev => ({
        x: prev.x + e.movementX,
        y: prev.y + e.movementY
      }));
    } else if (isCropping && selectedTool === 'crop') {
      const coords = screenToImageCoords(e.clientX, e.clientY);
      setCropEnd(coords);
    }
  };

  const handleMouseUp = () => {
    setIsPanning(false);
    if (isCropping && cropStart && cropEnd) {
      // Apply crop if selection is valid
      const cropData = {
        x: Math.min(cropStart.x, cropEnd.x),
        y: Math.min(cropStart.y, cropEnd.y),
        width: Math.abs(cropEnd.x - cropStart.x),
        height: Math.abs(cropEnd.y - cropStart.y)
      };

      if (cropData.width > 10 && cropData.height > 10) {
        onCropApply(cropData);
      }

      // Reset crop selection
      setCropStart(null);
      setCropEnd(null);
      setIsCropping(false);
    }
  };

  const handleWheel = (e: React.WheelEvent) => {
    e.preventDefault();
    const delta = e.deltaY > 0 ? 0.9 : 1.1;
    const newZoom = Math.max(0.1, Math.min(zoom * delta, 5));
    onZoomChange(newZoom);
  };

  if (!imageUrl) {
    return (
      <div className="flex-1 flex items-center justify-center bg-muted/20" data-testid="canvas-empty">
        <div className="text-center space-y-2">
          <div className="w-16 h-16 rounded-full bg-muted flex items-center justify-center mx-auto">
            <Maximize2 className="w-8 h-8 text-muted-foreground" />
          </div>
          <p className="text-muted-foreground">No image loaded</p>
        </div>
      </div>
    );
  }

  return (
    <div ref={containerRef} className="flex-1 relative overflow-hidden bg-muted/20" data-testid="canvas-container">
      {/* Canvas */}
      <div 
        className={`w-full h-full flex items-center justify-center relative ${
          selectedTool === 'crop' ? 'cursor-crosshair' : 'cursor-grab active:cursor-grabbing'
        }`}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        onWheel={handleWheel}
        style={{
          transform: `translate(${canvasOffset.x}px, ${canvasOffset.y}px)`
        }}
      >
        <div className="relative">
          <canvas
            ref={canvasRef}
            className="shadow-lg border border-border/50"
            data-testid="canvas-image"
          />
          {selectedTool === 'crop' && (
            <canvas
              ref={overlayCanvasRef}
              className="absolute top-0 left-0 pointer-events-none"
              data-testid="canvas-crop-overlay"
            />
          )}
        </div>
      </div>

      {/* Zoom Controls */}
      <div className="absolute bottom-4 right-4 flex items-center space-x-2 bg-card/95 border border-border rounded-lg p-2">
        <Button
          size="icon"
          variant="ghost"
          onClick={handleZoomOut}
          data-testid="button-zoom-out"
        >
          <ZoomOut className="w-4 h-4" />
        </Button>

        <span className="text-sm font-medium min-w-12 text-center" data-testid="text-zoom-display">
          {Math.round(zoom * 100)}%
        </span>

        <Button
          size="icon"
          variant="ghost"
          onClick={handleZoomIn}
          data-testid="button-zoom-in"
        >
          <ZoomIn className="w-4 h-4" />
        </Button>

        <Button
          size="icon"
          variant="ghost"
          onClick={handleFitToScreen}
          data-testid="button-fit-screen"
        >
          <Maximize2 className="w-4 h-4" />
        </Button>
      </div>
    </div>
  );
}

